CREATE DATABASE eshopper CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci;
USE eshopper;

create table `order` (`id` bigint not null auto_increment, `adress` varchar(255), `city` varchar(255), `first_name` varchar(255), `last_name` varchar(255), `date` datetime(6),`phone` varchar(255), `user_id` integer not null, primary key (`id`))ENGINE=InnoDB DEFAULT CHARSET=utf8;
create table `cart_item` (`id` bigint not null auto_increment, `product_id` integer not null,`user_id` integer not null, `quantity` integer not null, `order_id` bigint, primary key (`id`))ENGINE=InnoDB;
create table `product` (`id` integer not null auto_increment, `category` varchar(255), `gender_category` varchar(255), `img` varchar(255), `name` varchar(255), `price` bigint, primary key (`id`))ENGINE=InnoDB;
create table `users` (`id` integer not null auto_increment, `email` varchar(255), `password` varchar(255), `username` varchar(255), primary key (`id`))ENGINE=InnoDB;
alter table `cart_item` add foreign key (`order_id`) references `order` (`id`);
alter table `cart_item` add foreign key (`product_id`) references `product` (`id`);
alter table `cart_item` add foreign key (`user_id`) references `users` (`id`);
alter table `order` add foreign key (`user_id`) references `users` (`id`);


